$(document).ready(function() {
    var $element = $('#container_cuerpo_producto');

    if ($element.length > 0) {
        $(window).scroll(function() {
            var tabs = $('#container_cuerpo_producto').offset().top;
            var userWindow = $(window).scrollTop();
            var resultDistance = tabs - userWindow;
            
            if (resultDistance < 0) {
                $('#container_cuerpo_producto .product-links').addClass('fixed-prod-tabs');
            } else {
                $('#container_cuerpo_producto .product-links').removeClass('fixed-prod-tabs');
            }
        });
    }
    

    accionesMobile()
  
    var windowWidth = $(window).width();
    $(window).on('resize', function() {
        windowWidth = $(window).width();
        accionesMobile()
    });
    function accionesMobile() {
        if ($(window).width() <= 768) {
            $('.mobile-products-content-page').addClass('active');
            $('#container_cuerpo_producto').hide();
            $('#descriptionProduct').html($('#product-description .cms-content').html());
            $('#descriptionProduct').append($('.prod-img').html());
            $('#descriptionProduct').append($('.prod-actions').html());
            $('#beneficiosProduct').html($('#product-benefits .beneficios-previews').html());
            $('#requisitosProduct').html($('#product-requisites .requisites-list-wrap').html());
            $('#tarifasProduct').html($('#product-rates .cms-content').html())
        } else {
            $('.product-links').show();
            $('.mobile-products-content-page').removeClass('active')
        }
    }
    $("h2:contains('Descripción')").css("display", "none");
});